const Sidebar = () => {
  const [openMenu, setOpenMenu] = useState({});
  const [selectedItem, setSelectedItem] = useState(null);

  const handleToggle = (label) => {
    setOpenMenu((prevState) => ({
      ...prevState,
      [label]: !prevState[label],
    }));
  };

  const handleSelect = (label) => {
    setSelectedItem(label);
  };

  return (
    <Drawer
      variant="permanent"
      anchor="left"
      sx={{
        zIndex: "9",
        width: "275px", // Increased width
        flexShrink: 0,
        "& .MuiDrawer-paper": { width: "275px" },
      }}
    >
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          padding: "16px",
        }}
      >
        <img
          src={logo}
          alt="Logo"
          style={{ width: "100%", maxWidth: "280px", height: "auto" }} // Adjusted max width for the logo
        />
      </Box>
      <Box
        sx={{
          overflowY: "auto",
          overflowX: "hidden",
          marginTop: "20px",
          "&::-webkit-scrollbar": { display: "none" },
        }}
      >
        <List>
          {menuItems.map((item, index) => (
            <React.Fragment key={index}>
              <ListItem
                button
                onClick={() => {
                  item.children
                    ? handleToggle(item.label)
                    : handleSelect(item.label);
                }}
                sx={{
                  color: selectedItem === item.label ? "#FF8C00" : "#6c757d",
                  paddingLeft: "3rem",
                  marginTop: "5px",
                  paddingRight: "3rem",
                  minHeight: 40,
                  "&:hover": {
                    color: "#FF8C00",
                  },
                }}
              >
                <ListItemIcon
                  sx={{
                    minWidth: "48px",
                    backgroundColor: "transparent",
                    color: selectedItem === item.label ? "#FF8C00" : "#6c757d",
                    fontSize: "0.6rem",
                    "&:hover": {
                      backgroundColor: "#FFF9F2",
                      borderRadius: "50%",
                    },
                  }}
                >
                  {item.icon}
                </ListItemIcon>
                <ListItemText
                  primary={item.label}
                  primaryTypographyProps={{
                    sx: {
                      fontWeight:
                        selectedItem === item.label ? "bold" : "normal",
                      marginLeft: "-1rem", // Adjust this to reduce the gap
                      display: "flex",
                      alignItems: "center",
                      fontSize: "0.8rem",
                    },
                  }}
                />
                {item.children &&
                  (openMenu[item.label] ? <ExpandLess /> : <ExpandMore />)}
              </ListItem>
              {item.children && (
                <Collapse
                  in={openMenu[item.label]}
                  timeout="auto"
                  unmountOnExit
                >
                  <List
                    component="div"
                    disablePadding
                    sx={{
                      pl: 4,
                      listStyleType: "disc",
                      listStylePosition: "inside",
                    }}
                  >
                    {item.children.map((child, childIndex) => (
                      <ListItem
                        button
                        key={childIndex}
                        sx={{
                          pl: 5,
                          color:
                            selectedItem === child.label
                              ? "#FF8C00"
                              : "#6c757d",
                          "&:hover": { color: "#FF8C00" },
                        }}
                        onClick={() => handleSelect(child.label)}
                      >
                        <ListItemIcon
                          sx={{
                            minWidth: "20px",
                            backgroundColor: "transparent",
                            color:
                              selectedItem === child.label
                                ? "#FF8C00"
                                : "#6c757d",
                            "&:hover": {
                              backgroundColor: "#FFF9F2",
                              borderRadius: "50%",
                            },
                          }}
                        >
                          {child.icon}
                        </ListItemIcon>
                        <ListItemText
                          primary={child.label}
                          primaryTypographyProps={{
                            sx: {
                              fontSize: "0.7rem",
                            },
                          }}
                        />
                      </ListItem>
                    ))}
                  </List>
                </Collapse>
              )}
            </React.Fragment>
          ))}
        </List>
      </Box>
    </Drawer>
  );
};

export default Sidebar;
